var x11 = parseInt(document.getElementById("param1").value);
var x12 = parseInt(document.getElementById("param2").value);
var f1 = parseInt(document.getElementById("param3").value);

var x21 = parseInt(document.getElementById("param4").value);
var x22 = parseInt(document.getElementById("param5").value);
var f2 = parseInt(document.getElementById("param6").value);

var x31 = parseInt(document.getElementById("param7").value);
var x32 = parseInt(document.getElementById("param8").value);
var f3 = parseInt(document.getElementById("param9").value);

var ctx = document.getElementById("myChart_idBox4");

media1 = ((x11 + x12)/2)*f1
media2 = ((x21 + x22)/2)*f2
media3 = ((x31 + x32)/2)*f3

media = (media1 + media2 + media3)/(f1+f2+f3)

desv1 = ((x11 + x12)/2)*((x11 + x12)/2)*f1
desv2 = ((x21 + x22)/2)*((x21 + x22)/2)*f2
desv3 = ((x31 + x32)/2)*((x31 + x32)/2)*f3

desviacion = Math.sqrt(((desv1+desv2+desv3)/(f1+f2+f3))- (media*media))

var myChart = new Chart(ctx, {
    type: 'bar',
    data: {
        labels: ['Media','Desviación típica'],
        datasets: [{
            label: ['Media','Desviación típica' ],
            data: [media,desviacion],
            borderColor: [
                'rgba(255,99,132,1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)'
            ],
            backgroundColor: [
                'rgba(255,99,132,1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)'
            ],
            borderWidth: 1
        }]
    },
    options: {
        legend: { display: false },
        title: {
            display: true,
            text: 'Media , Desviación típica'
        },
        maintainAspectRatio: false,
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero:true
                }
            }]
        }
    }
});
"";

var exder = parseInt(document.getElementById("param2").value);
var exizq = parseInt(document.getElementById("param1").value);
var a = parseInt(document.getElementById("param3").value);
var b = parseInt(document.getElementById("param4").value);
var ctx = document.getElementById("myChart_idBox4");

var x_values_der = []
var x_values_izq = []
var x_values = []

for(i = -6; i < exizq; i=i+0.01){
    x_1 = i
    y_1 = (2*i)+1
    x_values_izq.push({
        x: x_1,
        y: y_1
    })
}

for(i = exizq; i <= exder; i=i+0.01){
    x_1 = i
    y_1 = a*(i*i) + b*i
    x_values.push({
        x: x_1,
        y: y_1
    })
}

for(i = exder + 0.01; i < exder + 2; i=i+0.01){
    x_1 = i
    y_1 = i - 4
    x_values_der.push({
        x: x_1,
        y: y_1
    })
}

var data = {
	labels: [-6,-5,-4,-3,-2,-1,01,2,3,4,5,6],
    datasets: [{
        label: "2x + 1",
        borderColor: "#21618c",
        data: x_values_izq,
        fill: false,
        spanGaps: false
    },
    {
        label: "ax^2 + bx",
        borderColor: "#FF0000",
        data: x_values,
        fill: false,
        spanGaps: false
    },
    {
        label: "x - 4",
        borderColor: "#000000",
        data: x_values_der,
        fill: false,
        spanGaps: false
    },
    ]
};

var myChart = new Chart(ctx, {
    type: 'line',
    data: data,
    options: {
        elements: { point: { radius: 0 } },
        scales: {
            xAxes: [{
                type: 'linear',
                position: 'bottom'
            }]
        }
    }
});
